		// Load events and try to enrol
		System.out.println("Loading events...");
		indy.getNextEventContexts(limit).stream().map(EventContext::loadEvent)
			.filter(event -> event instanceof FutureEvent).map(event -> (FutureEvent) event)
			// Filtered out all events that are in the past or holidays, casted all events
			// to FutureEvent
			.forEach(event -> {
				System.out.println("--------------------------------");
				System.out.println("Found event " + event.getEventContext().getDate() + "...");
				System.out.println("Checking entries...");

				// Choose random activity for the day
				Activity randomActivity = activities.get(ThreadLocalRandom.current().nextInt(0, activities.size()));
				System.out.println("Choosing activity: " + randomActivity);

				for (int hour : event.getEventContext().getHours()) {
					if (!event.getEntries().get(hour).isPresent()) {
						RequestEntry requestEntry = config.getEntriesForDay(event).stream()
							.filter(entry -> entry.getHour() == hour).filter(Entry::isValid).findFirst()
							// Inject activity
							.map(entry -> {
								entry.setSubject(randomActivity.getSubject());
								entry.setActivity(randomActivity.getActivityDescription());
								return entry;
							})
							.map(Entry::getEntry)
							.orElse(null);

						if (requestEntry == null) {
							System.out.println("no valid entry found for hour " + hour + "...");
							continue;
						}

						System.out.println("Trying to enrol for hour " + hour + "...");
						event.enrol(requestEntry);
					} else {
						System.out.println("Already enrolled for hour " + hour + "...");
					}
				}
				System.out.println("--------------------------------");
			});

		System.out.println("\nLog out of indy service...");
		indy.logout();

		System.out.println("Done.");
